fixes r1 r2 :: real
assumes h0 : "r1 = 1"
and h1 : "r2 = 2"
shows  "let s = 2 * r2 + 2 * r1; in s powr 2 = 22 + 12 * sqrt 2"
 proof- 
  show ?thesis sledgehammer

