theorem
fixes A A' A'' :: "real*real"
assumes h0 : "A = (3,3)"
and h1 : "A' = (fst A, -(snd A))"
and h2 : "A'' = (fst A' - 2, snd A')"
shows  "fst A'' + snd A'' = -2"
 proof- 
  show ?thesis sledgehammer

