B = (1,3);
O = (0,0);
C = extension(O, reflect(O,B)*(A), A, B);

draw(O--A,Arrow(6));
draw(O--B,Arrow(6));
draw(O--C,Arrow(6));
draw((-3,0)--(3,0));
draw((0,-1)--(0,6));
draw(interp(A,C,-0.1)--interp(A,C,1.1),dashed);

label("$\mathbf{a}$", A, NE);
label("$\mathbf{b}$", B, NE);
label("$\mathbf{c}$", C, NE);
[/asy]

