assumes h0 : "r1 = 3"
and h1 : "r2 = 8"
and h2 : "a = 5"
and h3 : "b = (a * r2) / (r1 + r2)"
and h4 : "c = (a * r1) / (r1 + r2)"
and h5 : "d = b - c"
