a_2.formal problem:
consts f :: "nat \<times> nat \<Rightarrow> nat"
function f_def: "f (i,j) = (if i = 0 \<and> j \<in> {0..4} then j mod 5 + 1 
                          else if i \<ge> 1 \<and> j = 0 then f(i-1,1) 
                          else if i \<ge> 1 \<and> j \<in> {1..4} then f(i-1, f(i,j-1)) 
                          else undefined)"
by auto

theorem
assumes h0 : "\<forall> i j. f(i,j) = (if i = 0 \<and> j \<in> {0..4} then j mod 5 + 1 
                              else if i \<ge> 1 \<and> j = 0 then f(i-1,1) 
                              else if i \<ge> 1 \<and> j \<in> {1..4} then f(i-1, f(i,j-1)) 
                              else undefined)"
shows  "f(2015,2) = 1"
