draw(incircle(A,B,C));
draw(E--I--F);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
label("$E$", E, NE);
label("$F$", F, NW);
label("$I$", I, S);
[/asy]

