theorem
fixes A B C D E F M N P Q :: "real^3" and volume :: "(real^3) \<Rightarrow> (real^3) \<Rightarrow> (real^3) \<Rightarrow> real"
assumes h0 : "A $ 3 = B $ 3"
and h1 : "C $ 3 = D $ 3"
and h2 : "E $ 3 = F $ 3"
and h3 : "D $ 3 - E $ 3 = 16"
and h4 : "norm (A - B) = 12"
and h5 : "norm (C - B) = 12"
and h6 : "norm (A - C) = 12"
and h7 : "M $ 3 = D $ 3"
and h8 : "N $ 3 = D $ 3"
and h9 : "P $ 3 = C $ 3"
and h10 : "Q $ 3 = C $ 3"
and h11 : "norm (D - M) = 4"
and h12 : "norm (D - N) = 2"
and h13 : "norm (C - Q) = 8"
and h14 : "M $ 2 = E $ 2"
and h15 : "N $ 2 = F $ 2"
and h16 : "P $ 2 = B $ 2"
and h17 : "Q $ 2 = A $ 2"
and h18 : "D \<noteq> M"
and h19 : "D \<noteq> N"
and h20 : "C \<noteq> Q"
and h21 : "D \<noteq> E"
and h22 : "D \<noteq> F"
and h23 : "C \<noteq> B"
