draw((0,0)--(10,10)--(-2,8)--cycle);
draw((-2,8)--(0,0)--(-11,10)--cycle);
draw((-2,8)--(0,23)--(10,10));
draw((0,23)--(-11,10));
draw((0,23)--(2.5,13)--(10,10),dashed);
draw((2.5,13)--(-11,10),dashed);
draw((2.5,13)--(0,0),dashed);
[/asy]

