datatype cell = Bottom int | Middle int | Top int

fun sum_adjacent :: "cell \<Rightarrow> cell \<Rightarrow> cell" where
"sum_adjacent (Bottom a) (Bottom b) = Middle (a + b)" |
"sum_adjacent (Middle a) (Middle b) = Top (a + b)" |
"sum_adjacent _ _ = undefined"

