[asy]
draw((-6,0)--(3.5,0),Arrows);
draw((-4,3)--(2,-1.5),Arrows);
draw((3,3)--(-4.5,-4.5),Arrows);
draw((-4,1)--(2,-3.5),Arrows);
label("1",(1.5,0),N);
label("2",(-2.7,0),N);
label("$m_{2}$",(3,3),NE);
label("$m_{1}$",(3.5,0),E);
label("$l_{1}$",(2,-1.5),E);
label("$l_{2}$",(2,-3.5),E);
[/asy]

