[asy]unitsize(1cm);
pair A,B,C,D,G,F,O;
A=(-2.8,0); B=(2.8,0); C=(-1.4,1.4);
D=(1.4,1.4); G=(-2.8,2); F=(2.8,2);
O=(0,0);
draw(A--B);
draw(G--F);
draw(O--C);
draw(O--D);
fill(O--D--F--G--C--cycle,gray(0.6));
draw(A--(-2.8,-2));
draw(B--(2.8,-2));
label("2",(-2.8,-1),W);
label("2",(2.8,-1),E);
dot(A);
dot(B);
dot(C);
dot(D);
dot(G);
dot(F);
dot(O);
fill((-2,1.85)..C--G..cycle,white);
fill((2,1.85)..D--F..cycle,white);
label("$A$",A,W);
label("$B$",B,E);
label("$C$",C,SW);
label("$D$",D,SE);
label("$E$",G,N);
label("$F$",F,N);
label("$O$",O,S);
draw(Circle(A,2));
draw(Circle(B,2));
[/asy]

