B = (-1,parab(-1));
C = extension(A,B,(0,0),(0,1));

draw(graph(parab,-2,2));
draw(A--B);
draw((0,0)--(0,4));

dot("$A$", A, E);
dot("$B$", B, SW);
dot("$(0,c)$", C, NW);
