size(4cm);

dotfactor = 4;
defaultpen(linewidth(1)+fontsize(10pt));

pair A,B,C,D,M;
A = (0,1);
B = (1,1);
C = (1,0);
D = (0,0);
M = (.5,1.5);

draw(A..M..B--C--D--cycle);
draw(A--B);

dot("A",A,W);
dot("M",M,N);
dot("B",B,E);
dot("C",C,E);
dot("D",D,W);

draw(M--C,linetype("0 4"));

[/asy] The final Answer is 3\sqrt{10}
