pair A, B, C, D, E, O, P, Q, R;

A = dir(90);

B = dir(90 - 360/5);

C = dir(90 - 2*360/5);

D = dir(90 - 3*360/5);

E = dir(90 - 4*360/5);

O = (0,0);

P = (C + D)/2;

Q = (A + reflect(B,C)*(A))/2;

R = (A + reflect(D,E)*(A))/2;

