[asy]
import three;

size(220);
currentprojection = orthographic(0.5,0.3,0.2);

triple I = (1,0,0), J = (0,1,0), K = (0,0,1), O = (0,0,0);
triple V = (-1,0.2,0.5), W = (0,3,0.7), U = (-0.8,0.5,2);

