 *)

theorem
fixes a b f :: "real \<Rightarrow> real"
assumes h0 : "bij f"
and h1 : "f a = b"
and h2 : "f b = 4"
shows  "a - b = -2"
 proof- 
  show ?thesis sledgehammer

