theorem
fixes a b c d x :: real
assumes h0 : "\<forall>x. y x = a * sin(b*x + c) + d"
and h1 : "d = 1"
and h2 : "b = 3"
and h3 : "c = pi"
and h4 : "y x = 2"
shows  "a = 2"
 proof- 
  show ?thesis sledgehammer

