theorem
fixes R :: "real^3^3" and v :: "real^3"
assumes h0 : "R = (1/3, -2/3, 2/3, -2/3, 1/3, 2/3, 2/3, 2/3, 1/3)"
and h1 : "\<forall> v. R *v v = v - 2 * ((v \<bullet> (1, 1, -1)) / ((1, 1, -1) \<bullet> (1, 1, -1))) *\<^sub>R (1, 1, -1)"
shows  "R = (1/3, -2/3, 2/3, -2/3, 1/3, 2/3, 2/3, 2/3, 1/3)"
