label("Burgers", X, S);
Y=(-.17,-.7);
label("Ice Cream", Y, N);
label("$\frac{1}{5}$", Y, S);
Z=(.5, -.15);
label("Music", Z, N);
label("$\frac{3}{10}$", Z, S);
[/asy]

