draw((A.x + 1,A.y + 0.1)--(A.x + 1,A.y + 0.9));

draw((A.x + 0.1,A.y + 1)--(A.x + 0.9,A.y + 1));
}

int k = 0;

for(int i = 1; i <= 3; ++i){

for(int j = 0; j < i; ++j){

drawSquare((k,0));

++k;

}

draw((k+0.1,0.5)--(k+0.9,0.5),EndArrow);

++k;
}
label("$\cdots$",(k,0.5));
[/asy]

