draw((1,1)--(4,1));
label("$x$",(1,2),E);
label("$y$",(2,4),N);
pair a,b;
a = (0,4.31);
b = a + (4,0);
draw(a--a+(1.8,0));
draw(a+(2.2,0)--b);
draw(a+(0,.09)--a-(0,.09));
draw(b+(0,.09)--b-(0,.09));
[/asy]

