size(200);
pair A, B, C, P, Q, R, S;
R=(0,0);
Q=(-2,0);
S=(2,0);
P=(1,1.732);
B=(-5.73,-1);
C=(3.732,-1);
A=(1.366,3.098);
draw(A--B--C--A);
draw(circle(P, 1));
draw(circle(Q, 1));
draw(circle(R, 1));
draw(circle(S, 1));
label("A", A, N);
label("B", B, SW);
label("C", C, SE);
dot(P);
dot(Q);
dot(R);
dot(S);
label("P", P, N);
label("Q", Q, SW);
label("R", R, SW);
label("S", S, SE);
[/asy]

