[asy]
size(200);
draw(Circle((0,0),5),linewidth(1.2));
pair[] mins;
for(int i = 0; i < 60; ++i){

mins[i] = 4.5*dir(-6*i + 90);

dot(mins[i]);
}
for(int i = 1; i <= 12; ++i){

label((string)i,mins[5*i % 60],dir(-30*i - 90));
}

fill(Circle((0,0),0.25));
[/asy]

