theorem
fixes p q :: "int \<Rightarrow> int"
assumes h0 : "p(-4) = 2"
and h1 : "p(-3) = 1"
and h2 : "p(-2) = 0"
and h3 : "p(-1) = 1"
and h4 : "p(0) = 2"
and h5 : "p(1) = 1"
and h6 : "p(2) = 0"
and h7 : "p(3) = 1"
and h8 : "p(4) = 2"
and h9 : "\<forall>x. q x = - abs x"
shows  "q(p(-4)) + q(p(-3)) + q(p(-2)) + q(p(-1)) + q(p(0)) + q(p(1)) + q(p(2)) + q(p(3)) + q(p(4)) = -10"
 proof- 
  show ?thesis sledgehammer

