import olympiad;
size(7cm);

real x = 50; real y = 20;

pair q = (1, 0);
pair r = (0, 0);
pair p = intersectionpoints((10 * dir(x))--r, q--(shift(q) * 10 * dir(180 - x)))[0];
pair s = intersectionpoints(r--(r + 10 * (p - r)), 10 * dir(180 - 2 * x)--q)[0];

// Draw lines
draw(p--s--q--p--r--q);

// Label points
label("$R$", r, SW);
label("$Q$", q, SE);
label("$S$", s, N);
label("$P$", p, NW);

// Label angles
