 [asy] fill( (-1,-1)-- (1,-1) -- (1,1) -- (-1,1)--cycle, gray); fill( Circle((1,1), 1.2), white); fill( Circle((-1,-1), 1.2), white); fill( Circle((1,-1),1.2), white); fill( Circle((-1,1), 1.2), white);
