theorem
fixes base height :: real
assumes h0 : "base = 8"
and h1 : "height = 10"
and h2 : "let area = (1/2) * base * height; piece_area = area / 8 in \<forall>i \<in> {1..8}. \<exists>piece_base piece_height. piece_base = base / i \<and> piece_height = 2 * piece_area / piece_base"
shows  "let max_piece_base = Max {base / i |i. i \<in> {1..8}};
           max_piece_height = 2 * ((1/2) * base * height) / 8 / max_piece_base in
       max_piece_base + 2 * sqrt (max_piece_height powr 2 + (max_piece_base / 2) powr 2) = 22.21"
 proof- 
  show ?thesis sledgehammer
