assumes h0 : "A = 0"
and h1 : "B = 8 * sqrt 5"
and h2 : "D = 8 * sqrt 5"
and h3 : "C = 8 * sqrt 5 + 32 * sqrt 2"
and h4 : "AB = 24"
and h5 : "sin A = 2/3"
and h6 : "sin C = 1/3"
and h7 : "ADB = 90"
shows  "DC = 32 * sqrt 2"
