pair pA, pB, pC, pD, pE;
pA = (0, 0);
pB = pA + 6 * dir(0);
pC = pA + 10 * dir(90);
pD = pB + 6 * dir(90);
pE = (6 * pA + 10 * pD) / 16;
draw(pA--pB--pC--pA);
draw(pA--pB--pD--pA);
